package com.aspose.external.barcode.tester.releases.v25_1;

import com.aspose.barcode.licensing.LicenseType;
import org.testng.Assert;
import org.testng.annotations.BeforeMethod;
import org.testng.annotations.Test;
import utils.LicenseAssistant;

import java.io.ByteArrayOutputStream;
import java.util.Base64;

public class BARCODEJAVA1327
{
    @BeforeMethod
    public void setUp()
    {
        LicenseAssistant.setupLicense(LicenseType.Total);
    }

    @Test
    public void test()
    {
        String expected = "";
        BarcodeGenerator generator = new BarcodeGenerator(EncodeTypes.QR);
        BaseGenerationParameters params = generator.getParameters();

        params.setAutoSizeMode(AutoSizeMode.NEAREST);
        params.getImageHeight().setMillimeters(45);
        params.getImageWidth().setMillimeters(45);
        params.getBarcode().getCodeTextParameters().setLocation(CodeLocation.NONE);

        String value = "ï¼‘ ";
        generator.setCodeText(value);

        boolean exceptionThrown = false;
        try(ByteArrayOutputStream outStream = new ByteArrayOutputStream())
        {
            generator.save(outStream, BarCodeImageFormat.EMF);
            String actual = Base64.getEncoder().encodeToString(outStream.toByteArray());
            Assert.assertEquals(expected , actual);

        } catch (Exception e)
        {
            exceptionThrown = true;
        }
        Assert.assertFalse(exceptionThrown);
    }
}
