        @Test
        //[Category("Aztec")]
    public void test_Issue36259_aztecRune_gif_Aztec()
    {
        BarCodeReader reader = new BarCodeReader(Global.pathCombine(this.folder, "Issue36259\\aztecRune.gif"), DecodeType.AZTEC);
        {
            Assert.assertEquals(reader.readBarCodes().length, 1);
            Assert.assertEquals("171", reader.getFoundBarCodes()[0].getCodeText());
        }
    }

